import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Truck, Clock, CheckCircle, AlertCircle, MessageSquare } from "lucide-react";
import { supabase } from "@/integrations/supabase/client";
import { useToast } from "@/hooks/use-toast";

interface DeliveryConfirmation {
  id: string;
  order_id: string;
  confirmation_code: string;
  vendor_id: string;
  delivered_at: string | null;
  delivery_driver: string | null;
  delivery_notes: string | null;
  status: 'pending' | 'confirmed' | 'failed';
  created_at: string;
}

interface DeliveryTrackerProps {
  orderId: string;
  vendorId: string;
}

export const DeliveryTracker: React.FC<DeliveryTrackerProps> = ({ orderId, vendorId }) => {
  const [delivery, setDelivery] = useState<DeliveryConfirmation | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isConfirming, setIsConfirming] = useState(false);
  const [driverName, setDriverName] = useState('');
  const [deliveryNotes, setDeliveryNotes] = useState('');
  const { toast } = useToast();

  useEffect(() => {
    fetchDeliveryInfo();
  }, [orderId, vendorId]);

  const fetchDeliveryInfo = async () => {
    try {
      const { data, error } = await supabase
        .from('delivery_confirmations')
        .select('*')
        .eq('order_id', orderId)
        .eq('vendor_id', vendorId)
        .single();

      if (error && error.code !== 'PGRST116') {
        throw error;
      }

      if (data) {
        setDelivery(data as DeliveryConfirmation);
        setDriverName(data.delivery_driver || '');
        setDeliveryNotes(data.delivery_notes || '');
      } else {
        // Create delivery confirmation entry if it doesn't exist
        await createDeliveryConfirmation();
      }
    } catch (error) {
      console.error('Error fetching delivery info:', error);
      toast({
        title: "Error",
        description: "Failed to load delivery information.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const createDeliveryConfirmation = async () => {
    try {
      // Let the database auto-generate the confirmation_code
      const { data, error } = await supabase
        .from('delivery_confirmations')
        .insert({
          order_id: orderId,
          vendor_id: vendorId,
          confirmation_code: '', // Will be auto-generated by trigger
        })
        .select()
        .single();

      if (error) throw error;
      setDelivery(data as DeliveryConfirmation);
    } catch (error) {
      console.error('Error creating delivery confirmation:', error);
    }
  };

  const confirmDelivery = async () => {
    if (!delivery) return;

    setIsConfirming(true);
    try {
      const { error } = await supabase
        .from('delivery_confirmations')
        .update({
          status: 'confirmed',
          delivered_at: new Date().toISOString(),
          delivery_driver: driverName || null,
          delivery_notes: deliveryNotes || null,
        })
        .eq('id', delivery.id);

      if (error) throw error;

      await fetchDeliveryInfo();
      
      toast({
        title: "Delivery Confirmed",
        description: "Delivery has been successfully confirmed.",
      });
    } catch (error) {
      console.error('Error confirming delivery:', error);
      toast({
        title: "Error",
        description: "Failed to confirm delivery.",
        variant: "destructive",
      });
    } finally {
      setIsConfirming(false);
    }
  };

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="flex items-center justify-center">
            <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-primary"></div>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (!delivery) {
    return (
      <Card>
        <CardContent className="p-6">
          <p className="text-muted-foreground text-center">No delivery information available.</p>
        </CardContent>
      </Card>
    );
  }

  const getStatusIcon = () => {
    switch (delivery.status) {
      case 'confirmed':
        return <CheckCircle className="h-5 w-5 text-green-500" />;
      case 'failed':
        return <AlertCircle className="h-5 w-5 text-red-500" />;
      default:
        return <Clock className="h-5 w-5 text-yellow-500" />;
    }
  };

  const getStatusColor = () => {
    switch (delivery.status) {
      case 'confirmed':
        return 'default';
      case 'failed':
        return 'destructive';
      default:
        return 'secondary';
    }
  };

  return (
    <Card className="border-border">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Truck className="h-5 w-5" />
          Delivery Tracking
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-2">
            {getStatusIcon()}
            <span className="font-medium">Status:</span>
          </div>
          <Badge variant={getStatusColor()}>
            {delivery.status.charAt(0).toUpperCase() + delivery.status.slice(1)}
          </Badge>
        </div>

        <div className="bg-accent p-3 rounded-lg">
          <p className="text-sm font-medium">Confirmation Code</p>
          <p className="text-xl font-mono font-bold text-primary">{delivery.confirmation_code}</p>
          <p className="text-xs text-muted-foreground mt-1">
            Text this code to (555) 123-DELIVER to confirm delivery
          </p>
        </div>

        {delivery.status === 'pending' && (
          <div className="space-y-3">
            <div>
              <label className="text-sm font-medium">Driver Name (Optional)</label>
              <Input
                value={driverName}
                onChange={(e) => setDriverName(e.target.value)}
                placeholder="Enter driver name"
                className="mt-1"
              />
            </div>

            <div>
              <label className="text-sm font-medium">Delivery Notes (Optional)</label>
              <Textarea
                value={deliveryNotes}
                onChange={(e) => setDeliveryNotes(e.target.value)}
                placeholder="Any special delivery notes or instructions"
                className="mt-1"
                rows={2}
              />
            </div>

            <Button 
              onClick={confirmDelivery} 
              disabled={isConfirming}
              className="w-full"
            >
              {isConfirming ? 'Confirming...' : 'Confirm Delivery'}
            </Button>
          </div>
        )}

        {delivery.status === 'confirmed' && delivery.delivered_at && (
          <div className="space-y-2 pt-2 border-t border-border">
            <p className="text-sm">
              <span className="font-medium">Delivered:</span>{' '}
              {new Date(delivery.delivered_at).toLocaleString()}
            </p>
            {delivery.delivery_driver && (
              <p className="text-sm">
                <span className="font-medium">Driver:</span> {delivery.delivery_driver}
              </p>
            )}
            {delivery.delivery_notes && (
              <p className="text-sm">
                <span className="font-medium">Notes:</span> {delivery.delivery_notes}
              </p>
            )}
          </div>
        )}

        <div className="pt-2 border-t border-border">
          <p className="text-xs text-muted-foreground">
            Delivery confirmation helps track vendor performance and ensures customer satisfaction.
          </p>
        </div>
      </CardContent>
    </Card>
  );
};